import { execSync } from "node:child_process";

import { createGitignore, initPackageJson } from './init-files.js';

export const createmicrofrontend = async ({ type }) => {
  await initPackageJson();
  createGitignore();
  execSync(`npm install @brojs/frontend-init`, { stdio: "inherit" });
  execSync(`npx brojs-f-init ${type}`, { stdio: "inherit" });
};
